import React, { useState, useEffect, useRef } from 'react';
import Autocomplete from "react-google-autocomplete";
import { Map, GoogleApiWrapper, InfoWindow, Marker } from 'google-maps-react';

const API_KEY = "'AIzaSyDE19jz0Ojpx2q-mepUlNKjlH5mUvrkHsM'";

const mapStyles = {
  width: '70%',
  height: '100%'
};

var markers = [
  {
    id: 1,
    name: "Paris",
    position: { lat: 48.856788489137216, lng: 2.3521950779098466 }
  },
  {
    id: 2,
    name: "Tokyo",
    position: { lat: 35.68060395408808, lng: 139.7690013067459 }
  },
  {
    id: 3,
    name: "Pékin",
    position: { lat: 39.90445096377794, lng: 116.40744327976226 }
  },
  {
    id: 4,
    name: "New York",
    position: { lat: 40.712776, lng: -74.005974 }
  },
  {
    id: 5,
    name: "Mexique",
    position: { lat: 23.63480446746952, lng: -102.55273572023772}
  }
];


function TodoForm(props) {
  const [input, setInput] = useState(props.edit ? props.edit.value : '');

  state = {
    showingInfoWindow: false,  // Hides or shows the InfoWindow
    activeMarker: {},          // Shows the active marker upon click
    selectedPlace: {} ,         // Shows the InfoWindow to the selected place upon a marker


    lat: 0,
      lng: 0,
  };

  onMarkerClick = (props, marker, e) =>
  this.setState({
    selectedPlace: props,
    activeMarker: marker,
    showingInfoWindow: true
  });

onClose = props => {
  if (this.state.showingInfoWindow) {
    this.setState({
      showingInfoWindow: false,
      activeMarker: null
    });
  }
};

  const inputRef = useRef(null);


  useEffect(() => {
    inputRef.current.focus();
  });

  const handleChange = e => {
    setInput(e.target.value);
  };

  const handleSubmit = e => {
    e.preventDefault();
    props.onSubmit({
      id: Math.floor(Math.random() * 10000),
      text: input
    });
    setInput('');
  };


  return (
    <form onSubmit={handleSubmit} className='todo-form'>
      {props.edit ? (
        <>
          <input
            placeholder='Update your item'
            value={input}
            onChange={handleChange}
            name='text'
            ref={inputRef}
            className='todo-input edit'
          />
          <button onClick={handleSubmit} className='todo-button edit'>
            Update
          </button>
        </>
      ) : (
        <>

  <Map
        google={this.props.google}
        zoom={3}
        style={mapStyles}
        initialCenter={{

          lat: 7,
          lng: 7


        }}
      >

       {markers.map(({id, name, position }) => (
         <Marker
         key={id}
         position={position}
         onClick={this.onMarkerClick}
         name={name}
       ></Marker>
       ))}

      <Marker position={{ lat, lng }} onClick={this.onMarkerClick} name="Votre position"/>


        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <h4>{this.state.selectedPlace.name}</h4>
          </div>
        </InfoWindow>
      </Map>

      <Autocomplete
    apiKey= {API_KEY}
    placeholder='Ajouter une ville'
    onChange={handleChange}
    name='text'
    className='todo-input'
    value = {input}
    ref={inputRef}
    onPlaceSelected={(input) =>{ 

    console.log(input.address_components[0].long_name)
    console.log(input)

    }}

/>;

          <button onClick={handleSubmit} className='todo-button'>
            Ajouter
          </button>
        </>
      )}
    </form>
  );
}

export default TodoForm;
